SELECT * FROM ( SELECT ifnull(std.id, s.tenant_code) tenant_id, s.user_info_id user_info_id, ( SELECT COUNT(1) FROM oa_loan o, user_data_info u WHERE o.user_info_id = u.id AND o.tenant_id = ifnull(std.id, s.tenant_code) AND o.id IN ( SELECT busi_id FROM Audit_Node_Inst i, audit_user u WHERE u.target_Obj_Id = s.user_info_id AND u.node_id = i.node_id AND i.AUDIT_CODE IN ( 21000023 ) AND i.TENANT_ID = ifnull(std.id, s.tenant_code) AND i.AUDIT_RESULT = 0 ) AND o.launch = 2 AND o.loan_subject IN (
#{btnId}
) ) AS count FROM sys_user s left join sys_tenant_def std on std.admin_user = s.user_info_id ) AS top WHERE top.tenant_id IS NOT NULL

