mapper.select:
SELECT o.id , o.oil_car_num AS oilCarNum, u.service_name AS companyName, u.service_user_id AS serviceUserId, o.oil_Card_Status AS oilCardStatus, o.pledge_Order_Id AS pledgeOrderId, rel.product_id AS productId, u.service_type serviceType, o.oil_card_type oilCardType, rel.state AS servState, u.service_user_id AS userId, o.card_type AS cardType, o.card_balance AS cardBalance, ov.plateNumber AS bindVehicle FROM oil_card_management o LEFT JOIN service_info u ON u.service_user_id = o.User_id LEFT JOIN ( SELECT a.card_id, GROUP_CONCAT( VEHICLE_NUMBER SEPARATOR ',' ) AS plateNumber FROM oil_card_vehicle_rel a GROUP BY a.card_id ) AS ov ON o.id = ov.CARD_ID LEFT JOIN apply_card_rel rel ON rel.oil_card_num = o.OIL_CAR_NUM AND rel.oil_card_type = o.oil_card_type AND rel.is_recoveryed = 0
and o.tenant_id = #{tenantId}
and o.oil_car_num like concat('%',#{oilCardManagementVo.oilCarNum},'%')
and u.service_name like concat('%',#{oilCardManagementVo.companyName},'%')
and o.oil_Card_Status = #{oilCardManagementVo.oilCardStatus}
and o.card_type = #{oilCardManagementVo.cardType}
and o.oil_card_type = #{oilCardManagementVo.oilCardType}
and ov.plateNumber like concat('%',#{oilCardManagementVo.bindVehicle},'%')
ORDER BY o.oil_car_num
ORDER BY o.create_time desc

select *
from oil_card_management
where oil_car_num = #{oilCarNum}  and tenant_id= #{tenantId}

SELECT OIL_CARD_NUM as oilCardNum, GROUP_CONCAT(`VEHICLE_NUMBER` SEPARATOR ',') as vehicleNumber FROM oil_card_vehicle_rel WHERE tenant_id = #{tenantId}
AND OIL_CARD_NUM IN
#{num}
GROUP BY OIL_CARD_NUM

select *
from oil_card_management
where oil_car_num = #{oilCarNum}
  and tenant_id = #{tenantId}

SELECT SERVICE_USER_ID, SERVICE_NAME
from service_info
where SERVICE_TYPE = #{cardType}
  and tenant_id = #{tenantId}

SELECT ocm.*, ocv.vehicle_number as plateNumber FROM oil_card_vehicle_rel ocv left join oil_card_management as ocm on ocv.card_id=ocm.id where ocv.tenant_id=#{tenantId}
and ocv.vehicle_number like concat('%',#{plateNumber},'%')
and ocm.oil_card_status=#{oilCardStatus}
