SELECT moad.id AS id, moad.order_id AS orderId, moad.plate_number AS plateNumber, moad.start_date AS startDate, moad.end_date AS endDate, moad.line_detail AS lineDetail, moad.start_nand AS startNand, moad.start_eand AS startEand, moad.end_nand AS endNand, moad.end_eand AS endEand, moad.type AS type, moad.line_type AS lineType, moad.continue_time AS continueTime, moad.create_time AS createTime, moad.update_time AS updateTime, moad.tenant_id AS tenantId FROM
monitor_order_aging_abnormal_h moad,
monitor_order_aging_abnormal moad,
( SELECT o.order_id FROM
order_info_h o LEFT JOIN order_transit_line_info_h t
order_info o LEFT JOIN order_transit_line_info t
ON t.order_id = o.order_id
1 = 1
AND o.tenant_id = #{tenantId}
AND o.order_id = #{orderId}
AND (o.source_region= #{sourceRegion} OR t.region= #{sourceRegion})
AND (o.des_region= #{desRegion} OR t.region= #{desRegion})
GROUP BY order_id ) o, tenant_vehicle_rel v WHERE moad.plate_number = v.plate_number AND moad.order_id = o.order_id AND v.tenant_id = moad.tenant_id
AND moad.order_id = #{orderId}
AND moad.plate_number = #{plateNumber}
AND v.org_id = #{orgId}
AND moad.type = #{type}
AND moad.tenant_id = #{tenantId}
GROUP BY moad.plate_number

SELECT count(*) AS num FROM ( SELECT tvr.plate_number AS vehiclenum, tvr.org_id AS orgid, tvr.vehicle_class AS vehicleclass FROM tenant_vehicle_rel tvr WHERE tvr.tenant_id = #{tenantId} AND tvr.auth_state = 2 UNION ALL SELECT ttr.trailer_number AS vehiclenum, ttr.attached_root_org_two_id AS orgid, 6 AS vehicleclass FROM tenant_trailer_rel ttr WHERE ttr.tenant_id = #{tenantId} AND ttr.is_autit = 1 ) AS allvehicle
1 = 1
AND vehicleNum LIKE CONCAT('%',#{vehicleNum},'%')
AND orgId = #{orgId}
AND vehicleClass = #{type}

SELECT count(t.plate_number) AS count
SELECT t.plate_number AS platenumber, t.vehicle_code AS vehiclecode, car_driver_phone cardriverphone, t.create_date AS createdate, ifnull( tmp.vehicle_code, 0 ) AS ordervehiclecode
FROM tenant_vehicle_rel t LEFT JOIN ( SELECT max( arrivedate ), vehicle_code, plate_number, leisuredate, car_driver_phone FROM ( SELECT max( ifnull( car_arrive_date, date_add( depend_time, INTERVAL arrive_time HOUR ))) AS arrivedate, timestampdiff( HOUR, max( ifnull( car_arrive_date, date_add( depend_time, INTERVAL arrive_time HOUR ))), sysdate()) AS leisuredate, s.vehicle_code, s.plate_number, s.car_driver_phone FROM order_scheduler s, order_info o
o.order_id=s.order_id AND o.order_state>=7 AND o.order_state !=15
AND s.tenant_id = #{tenantId}
AND s.vehicle_class = #{vehicleClass}
AND s.vehicle_class IN ('1','3','2','4')
GROUP BY vehicle_code,plate_number UNION ALL SELECT max( ifnull( car_arrive_date, date_add( depend_time, INTERVAL arrive_time HOUR ))) AS arrivedate, timestampdiff( HOUR, max( ifnull( car_arrive_date, date_add( depend_time, INTERVAL arrive_time HOUR ))), sysdate()) AS leisuredate, s.vehicle_code, s.plate_number, s.car_driver_phone FROM order_scheduler_h s, order_info_h o
o.order_id=s.order_id AND o.order_state>=7 AND o.order_state !=15
AND s.tenant_id = #{tenantId}
AND s.vehicle_class = #{vehicleClass}
AND s.vehicle_class IN ('1','3','2','4')
GROUP BY vehicle_code,plate_number ) tmp WHERE 1 = 1 GROUP BY vehicle_code, plate_number ) tmp ON t.vehicle_code = tmp.vehicle_code
1=1
AND t.tenant_id = #{tenantId}
AND t.vehicle_class = #{vehicleClass} and t.auth_state = 2
AND t.vehicle_class IN ('1','3','2','4')
AND t.org_id = #{orgId}
AND t.plate_number = #{plateNumber}
AND (( tmp.leisuredate > #{hour} and tmp.leisuredate < #{hourEnd}) or ( tmp.vehicle_code IS NULL AND TIMESTAMPDIFF( HOUR, t.create_date, SYSDATE()) > #{hour}) and TIMESTAMPDIFF(hour,t.create_date,sysdate()) < #{hourEnd} )
AND ( tmp.leisuredate > #{hour} or ( tmp.vehicle_code IS NULL AND TIMESTAMPDIFF( HOUR, t.create_date, SYSDATE()) > #{hour}))

SELECT count( DISTINCT a.plate_number ) AS vehiclecount FROM vehicle_data_info AS a INNER JOIN tenant_vehicle_rel AS b ON b.vehicle_code = a.id LEFT JOIN tenant_vehicle_cert_rel AS c ON a.id = c.vehicle_code WHERE ( b.vehicle_class = 3 AND b.tenant_id = #{tenantId} AND ( c.tenant_id = a.tenant_id OR c.tenant_id IS NULL ) OR b.vehicle_class != 3 AND b.tenant_id = #{tenantId} AND ( c.tenant_id = #{tenantId} OR c.tenant_id IS NULL )) AND ( c.${fieldName} <= #{expireDate} OR c.${fieldName} IS NULL ) AND b.auth_state = #{state}
AND b.vehicle_class = #{vehicleClass}
AND a.plate_number= #{plateNumber}

SELECT outertab.maintain_dis AS maintaindis, outertab.maintain_warn_dis AS maintanwarndis, outertab.id AS vehiclecode, '6' AS vehicleclass, sum( outertab.distance ) AS distance, outertab.prev_maintain_time AS premaintaintime, outertab.trailer_plate AS platenumber, car_driver_phone AS cardriverphone FROM ( SELECT tvcr.maintain_dis, tvcr.prev_maintain_time, maintain_warn_dis, tvr.id, tmp.order_id, tmp.depend_time, tmp.trailer_plate, tmp.distance AS distance, tmp.car_driver_phone FROM tenant_trailer_rel tvcr, trailer_management tvr LEFT JOIN ( SELECT * FROM ( SELECT o.order_id, s.depend_time, s.trailer_plate, s.trailer_id, sum( s.distance ) AS distance, s.car_driver_phone FROM order_info o, order_scheduler s WHERE o.order_id = s.order_id AND o.tenant_id = #{tenantId}
AND s.trailer_plate = #{plateNumber}
AND o.order_state >= 7 GROUP BY s.trailer_id UNION ALL SELECT o.order_id, s.depend_time, s.trailer_plate, s.trailer_id, sum( s.distance ) AS distance, s.car_driver_phone FROM order_info_h o, order_scheduler_h s WHERE o.order_id = s.order_id AND o.tenant_id = #{tenantId}
AND s.trailer_plate = #{plateNumber}
AND o.order_state != 15 GROUP BY s.trailer_id ) tab GROUP BY tab.trailer_id ) tmp ON tvr.id = tmp.trailer_id WHERE tvr.tenant_id = #{tenantId}
AND tvcr.attached_root_org_two_id = #{orgId}
AND tvr.trailer_number = #{plateNumber}
AND ( tmp.depend_time > tvcr.prev_maintain_time OR tvcr.prev_maintain_time IS NULL ) AND tvr.id = tvcr.trailer_id GROUP BY tvcr.trailer_id ) outertab GROUP BY outertab.id HAVING sum( outertab.distance ) / 1000 >= ifnull( maintaindis, 0 )- ifnull( maintanwarndis, 0)

SELECT outertab.maintain_dis AS maintaindis, outertab.maintain_warn_dis AS maintanwarndis, outertab.vehicle_code AS vehiclecode, sum( outertab.distance ) AS distance, outertab.prev_maintain_time AS premaintaintime, outertab.plate_number AS platenumber, car_driver_phone AS cardriverphone FROM ( SELECT tvcr.maintain_dis, tvcr.prev_maintain_time, maintain_warn_dis, tvr.vehicle_code, tmp.order_id, tmp.depend_time, tmp.plate_number, tmp.distance AS distance, tmp.car_driver_phone FROM tenant_vehicle_cert_rel tvcr, tenant_vehicle_rel tvr LEFT JOIN ( SELECT * FROM ( SELECT o.order_id, s.depend_time, s.plate_number, s.vehicle_code, sum( s.distance ) AS distance, s.car_driver_phone FROM order_info o, order_scheduler s WHERE o.order_id = s.order_id AND o.tenant_id = #{tenantId} AND s.vehicle_class = 1
AND s.plate_number = #{plateNumber}
AND o.order_state >= 7 GROUP BY s.vehicle_code UNION ALL SELECT o.order_id, s.depend_time, s.plate_number, s.vehicle_code, sum( s.distance ) AS distance, s.car_driver_phone FROM order_info_h o, order_scheduler_h s WHERE o.order_id = s.order_id AND o.tenant_id = #{tenantId} AND s.vehicle_class = 1
AND s.plate_number = #{plateNumber}
AND o.order_state != 15 GROUP BY s.vehicle_code ) tab GROUP BY tab.vehicle_code ) tmp ON tvr.vehicle_code = tmp.vehicle_code WHERE tvr.tenant_id = #{tenantId} AND tvr.vehicle_class = 1
AND tvr.attached_root_org_two_id = #{orgId}
AND tvr.plate_number = #{plateNumber}
AND ( tmp.depend_time > tvcr.prev_maintain_time OR tvcr.prev_maintain_time IS NULL ) AND tvr.vehicle_code = tvcr.vehicle_code GROUP BY tvcr.vehicle_code ) outertab GROUP BY outertab.vehicle_code HAVING sum( outertab.distance ) / 1000 >= ifnull( maintaindis, 0 )- ifnull( maintanwarndis,0 )

