SELECT * FROM ( SELECT *, case when (TIMESTAMPDIFF(DAY,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate) <=0) then '已到期' when(TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)=0 and (DATE_FORMAT(effectiveDate, '%m' ) - DATE_FORMAT(now(), '%m' ))=0) then '本月到期' when (TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)=0) or (TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)=1 and (DATE_FORMAT(effectiveDate, '%d' ) - DATE_FORMAT(now(), '%d' ))=0) then '一个月' when (TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)=1) or (TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)=2 and (DATE_FORMAT(effectiveDate, '%d' ) - DATE_FORMAT(now(), '%d' ))=0) then '两个月' when (TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)=2) or (TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)=3 and (DATE_FORMAT(effectiveDate, '%d' ) - DATE_FORMAT(now(), '%d' ))=0) then '三个月' when (TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)=3 and (DATE_FORMAT(effectiveDate, '%d' ) - DATE_FORMAT(now(), '%d' ))>0) or (TIMESTAMPDIFF(MONTH,DATE_FORMAT(NOW(), '%Y-%m-%d'),effectiveDate)>3) then '三个月以后' end as leftDate FROM ( select * from ( SELECT a.plate_number AS plateNumber, a.licence_type AS licenceType, a.vehicle_status AS vehicleStatus, a.vehicle_length AS vehicleLength, DATE_FORMAT( c.annualreview_data, '%Y-%m-%d' ) AS annualreviewData, DATE_FORMAT( c.effective_date, '%Y-%m-%d' ) AS effectiveDate, c.annualreview_type AS annualreviewType, c.annualreview_cost AS annualreviewCost, c.service_provider AS name FROM vehicle_data_info AS a LEFT JOIN tenant_vehicle_rel AS b ON a.id = b.vehicle_code LEFT JOIN vehicle_annual_review AS c ON a.id = c.vehicle_id WHERE b.tenant_id = #{tenantId} AND b.vehicle_class = 1 and c.annualreview_data IS NOT NULL AND c.effective_date IS NOT NULL having 1 order by c.effective_date DESC) as f GROUP BY plateNumber,annualreviewType UNION ALL SELECT a.plate_number AS plateNumber, a.licence_type AS licenceType, a.vehicle_status AS vehicleStatus, a.vehicle_length AS vehicleLength, DATE_FORMAT( c.insurance_time, '%Y-%m-%d' ) AS annualreviewData, DATE_FORMAT( c.insurance_time_end, '%Y-%m-%d' ) AS effectiveDate, 3 AS annualreviewType, NULL AS annualreviewCost, NULL AS name FROM vehicle_data_info AS a LEFT JOIN tenant_vehicle_rel AS b ON a.id = b.vehicle_code LEFT JOIN tenant_vehicle_cert_rel AS c ON a.id = c.vehicle_code WHERE b.tenant_id = #{tenantId} AND b.vehicle_class = 1 UNION ALL SELECT a.plate_number AS plateNumber, a.licence_type AS licenceType, a.vehicle_status AS vehicleStatus, a.vehicle_length AS vehicleLength, DATE_FORMAT( c.busi_insurance_time, '%Y-%m-%d' ) AS annualreviewData, DATE_FORMAT( c.busi_insurance_time_end, '%Y-%m-%d' ) AS effectiveDate, 4 AS annualreviewType, NULL AS annualreviewCost, NULL AS name FROM vehicle_data_info AS a LEFT JOIN tenant_vehicle_rel AS b ON a.id = b.vehicle_code LEFT JOIN tenant_vehicle_cert_rel AS c ON a.id = c.vehicle_code WHERE b.tenant_id = #{tenantId} AND b.vehicle_class = 1 UNION ALL SELECT a.plate_number AS plateNumber, a.licence_type AS licenceType, a.vehicle_status AS vehicleStatus, a.vehicle_length AS vehicleLength, DATE_FORMAT( c.other_insurance_time, '%Y-%m-%d' ) AS annualreviewData, DATE_FORMAT( c.other_insurance_time_end, '%Y-%m-%d' ) AS effectiveDate, 5 AS annualreviewType, NULL AS annualreviewCost, NULL AS name FROM vehicle_data_info AS a LEFT JOIN tenant_vehicle_rel AS b ON a.id = b.vehicle_code LEFT JOIN tenant_vehicle_cert_rel AS c ON a.id = c.vehicle_code WHERE b.tenant_id = #{tenantId} AND b.vehicle_class = 1 ) AS t WHERE t.annualreviewData IS NOT NULL AND t.effectiveDate IS NOT NULL ORDER BY plateNumber,annualreviewType,effectiveDate) as g
g.leftDate !='三个月以后'
AND g.plateNumber = #{plateNumber}
