mapper.sql:
SELECT t.id, t.trailer_number as trailerNumber, t.source_province as sourceProvince, t.source_region as sourceRegion, t.is_state as isState, t.idle as idle, (SELECT COUNT( * ) FROM((SELECT o.order_id,o.trailer_id,o.tenant_id,i.order_state FROM order_scheduler o,order_info i WHERE o.order_id = i.order_id AND o.tenant_id = #{tenantId} AND i.order_state != 15 ) UNION (SELECT o.order_id,o.tariler_id,o.tenant_id,i.order_state FROM order_scheduler_h o,order_info_h i WHERE o.order_id = i.order_id AND o.tenant_id = #{tenantId} AND i.order_state != 15 ) ) AS tt WHERE tt.trailer_id = t.id )as usageCount,t.trailer_material as trailerMaterial,(SELECT COUNT( * ) FROM ( ( SELECT trailer_id FROM trailer_management_ver WHERE is_autit = 0 ) UNION ( SELECT id FROM trailer_management WHERE is_autit = 0 ) ) AS ver WHERE ver.trailer_id = t.id) as isAutit, t.source_county as sourceCounty, t.is_autit as auditState FROM trailer_management t, tenant_trailer_rel r
t.id = r.TRAILER_ID
and r.tenant_id = #{tenantId}
and t.trailer_number LIKE concat('%',#{trailerNumber},'%')
and t.trailer_material = #{trailerMaterial}
AND t.is_state = #{isState}
AND t.source_province = #{sourceProvince}
AND t.source_region = #{sourceRegion}
AND t.source_county = #{sourceCounty}
ORDER BY t.id DESC

