mapper.select:
select
    *
from user_data_info a ,tenant_user_rel b where a.id = b.user_id
                                           and a.id=#{id}
                                           and b.tenant_id=#{tenantId}
LIMIT 0,1

SELECT udi.id AS userid, udi.linkman AS linkman, udi.mobile_phone AS mobilephone, udi.identification AS identification, so.id AS operatorid, so.login_acct AS loginacct, so.PASSWORD AS PASSWORD, tsr.id AS staffid, tsr.employee_number AS employeenumber, tsr.staff_position AS staffposition, tsr.lock_flag AS lockflag, ( SELECT group_concat( o.role_id ) FROM sys_role_oper_rel o WHERE o.state = 1 AND o.operator_id = so.id AND o.tenant_id = tsr.tenant_id GROUP BY o.operator_id ) AS roleids, ( SELECT group_concat( u.org_id ) FROM sys_user_org_rel u WHERE u.state = 1 AND u.id = udi.id AND u.tenant_id = tsr.tenant_id GROUP BY u.id ) AS orgids FROM user_data_info udi INNER JOIN sys_user so ON udi.id = so.user_info_id INNER JOIN tenant_staff_rel tsr ON udi.id = tsr.user_info_id WHERE tsr.state =1
AND udi.id = #{staffDataInfoIn.userId}
AND so.LOGIN_ACCT LIKE CONCAT('%',#{staffDataInfoIn.loginAcct},'%')
AND udi.MOBILE_PHONE LIKE CONCAT('%',CONCAT(#{staffDataInfoIn.mobilePhone}),'%')
AND udi.LINKMAN = #{staffDataInfoIn.linkman}
AND tsr.EMPLOYEE_NUMBER LIKE CONCAT('%',#{staffDataInfoIn.employeeNumber},'%')
AND tsr.STAFF_POSITION LIKE CONCAT('%',#{staffDataInfoIn.staffPosition},'%')
AND tsr.LOCK_FLAG = #{staffDataInfoIn.lockFlag}
AND tsr.TENANT_ID = #{tenantId}
AND exists(SELECT * FROM sys_user_org_rel suor WHERE suor.TENANT_ID = tsr.TENANT_ID AND suor.USER_ID=udi.USER_ID AND suor.ORG_ID = #{staffDataInfoIn.orgId})
order by tsr.create_time desc
