mapper.sql:
SELECT va.id AS id, va.vehicle_id AS vehicleid, va.vehicle_code AS vehiclecode, va.accident_status AS accidentstatus, ( SELECT std.`name` FROM sys_tenant_def std WHERE std.id = va.tenant_id ) AS orgname, di.brand_model AS brandname, di.vehicle_model AS brandtype, va.insurance_company AS insurancecompany, va.insured AS insured, date_format( va.report_date, "%Y-%m-%d" ) AS reportdate, va.accident_date AS accidentdate, va.claim_amount AS claimamount, va.create_time AS createtime FROM vehicle_accident va JOIN vehicle_data_info di ON va.vehicle_id = di.id WHERE 1 = 1 AND va.tenant_id = #{tenantId}
AND va.vehicle_code LIKE CONCAT('%',#{vehicleCode},'%')
AND va.accident_status = #{accidentStatus}
AND va.report_date >= #{reportDateStart}
AND va.report_date <= #{reportDateEnd}
AND va.accident_date >= #{accidentDateStart}
AND va.accident_date <= #{accidentDateEnd}
AND va.create_time >= #{createDateStart}
AND va.create_time <= #{createDateEnd}
ORDER BY va.create_time DESC

SELECT va.id, vdi.user_id, vdi.plate_number AS plateNumber, vdi.licence_type AS licenceType, vdi.brand_model AS brandModel, vdi.vehicle_model AS vehicleModel, vdi.vehicle_status as vehicleStatus, va.accident_date AS accidentDate, va.accident_place AS accidentPlace, va.accident_cause AS accidentCause, va.accident_type AS accidentType, va.vehicle_damage AS vehicleDamage, va.other_damage AS otherDamage, va.road_loss AS roadLoss, va.material_amage AS materialAmage, va.wounding, va.accident_driver accidentDriver, va.main_driver AS mainDriver, va.insurance_company as insuranceCompany, va.Insured, va.report_date as reportDate, va.claim_amount as claimAmount, vdi.id as vehicle_code, tvr.vehicle_class as vehicleClass, tm.id as managementId, ( SELECT std.`name` FROM sys_tenant_def std WHERE std.id = va.tenant_id ) AS departmentName FROM vehicle_accident va LEFT JOIN vehicle_data_info vdi ON va.vehicle_code = vdi.plate_number LEFT JOIN tenant_vehicle_rel tvr on va.vehicle_code = tvr.vehicle_code LEFT JOIN trailer_management tm on tm.trailer_number=va.vehicle_code
1 = 1 AND va.tenant_id = #{tenantId}
and date(va.accident_date) >= #{monthTimeStart}
and date(va.accident_date) <= #{monthTimeEnd}
and vdi.licence_type = #{licenceType}
and va.accident_status = #{accidentStatus}
order by va.create_time desc

mapper.select:
SELECT
    SUM( claim_amount )
FROM
    vehicle_accident
WHERE
    accident_status = 3
    AND vehicle_id = #{vehicleCode}
    AND tenant_id = #{tenantId}
    AND DATE_FORMAT( create_time, '%Y-%m' ) = #{month}
