mapper.sql:
SELECT a.id recordId, a.VIOLATION_TIME violationTime, a.VIOLATION_CODE violationCode, a.LOCATION_NAME locationName, a.VIOLATION_ADDRESS violationAddress, a.VIOLATION_FINE violationFine, a.OVERDUE_FINE overdueFine, a.VIOLATION_POINT violationPoint, a.CATEGORY category, a.VIOLATION_WRIT_NO violationWritNo, a.TYPE type, a.USER_ID userId, a.PLATE_NUMBER plateNumber, a.LINK_MAN linkMan, a.MOBILE_PHONE mobilePhone, a.RECORD_STATE recordState, a.VIOLATION_REASON violationReason, b.VIOLATION_ORDER_ID violationOrderId, b.VIOLATION_ORDER_NUM violationOrderNum, b.ORDER_STATE orderState FROM violation_record a LEFT JOIN ( SELECT VIOLATION_ORDER_ID, RECORD_ID, VIOLATION_ORDER_NUM, ORDER_STATE FROM violation_order WHERE VIOLATION_ORDER_ID IN ( SELECT MAX( VIOLATION_ORDER_ID ) FROM violation_order GROUP BY RECORD_ID )) b ON a.id = b.RECORD_ID INNER JOIN tenant_vehicle_rel v ON a.VEHICLE_CODE = v.VEHICLE_CODE AND v.VEHICLE_CLASS != 3 WHERE 1 = 1 AND IF(a.RECORD_STATE = 2 AND (b.VIOLATION_ORDER_ID IS NULL OR b.VIOLATION_ORDER_ID = '' ),FALSE,TRUE) AND v.TENANT_ID = #{tenantId}
AND CATEGORY_ID = #{param.categoryId}
AND a.PLATE_NUMBER like CONCAT('%',#{param.plateNumber},'%')
AND a.LINK_MAN like CONCAT('%',#{param.linkMan},'%')
AND a.MOBILE_PHONE like CONCAT('%',#{param.linkmanPhone},'%')
AND date(a.VIOLATION_TIME) >= #{param.startTime}
AND date(a.VIOLATION_TIME) <= #{param.endTime}
AND a.LOCATION_NAME like CONCAT('%',#{param.violationCity},'%')
AND a.VIOLATION_CODE = #{param.peccancyCode}
AND a.TYPE = #{param.publicAndPrivateType}
AND a.id = #{param.recordId}
AND a.VIOLATION_WRIT_NO = #{param.violationWritNo}
ORDER BY a.VIOLATION_TIME DESC,a.id DESC

SELECT vr.id, vdi.plate_number as plateNumber, vdi.vehicle_status as vehicleStatus, vdi.licence_type as licenceType, vdi.vehicle_length as vehicleLength, vr.violation_time as violationTime, vr.renfa_time as renfaTime, vr.category_id as categoryId, vr.violation_fine as violationFine, vr.violation_point as violationPoint, vr.location_name as locationName, vr.violation_address as violationAddress, vr.violation_reason as violationReason, vr.record_state as recordState, tvr.vehicle_class as vehicleClass FROM violation_record vr LEFT JOIN vehicle_data_info vdi ON vr.vehicle_code = vdi.id Left join tenant_vehicle_rel tvr on vr.vehicle_code = tvr.vehicle_code
1 = 1 AND vr.tenant_id = #{tenantId}
and date(vr.violation_time) >= #{monthTimeStart}
and date(vr.violation_time) <= #{monthTimeEnd}
and vdi.licence_type = #{licenceType}
and vr.record_state = #{recordState}
