mapper.select:
SELECT udi.id AS userInfoId, udi.linkman AS linkman, udi.mobile_phone AS mobilephone, udi.identification AS identification, so.id AS operatorid, so.login_acct AS loginacct, so.PASSWORD AS PASSWORD, tsr.id AS staffid, tsr.employee_number AS employeenumber, tsr.staff_position AS staffposition, tsr.lock_flag AS lockflag, ( SELECT group_concat( o.role_id ) FROM sys_user_role o WHERE o.state = 1 AND o.user_id = so.id AND o.tenant_id = tsr.tenant_id GROUP BY o.user_id ) AS roleids, ( SELECT group_concat( u.org_id ) FROM sys_user_org_rel u WHERE u.state = 1 AND u.user_info_id = udi.id AND u.tenant_id = tsr.tenant_id
GROUP BY u.user_info_id ) AS orgids FROM user_data_info udi INNER JOIN sys_user so ON udi.id = so.user_info_id INNER JOIN tenant_staff_rel tsr on udi.id = tsr.user_info_id LEFT JOIN sys_user_org_rel r ON r.user_info_id = udi.id WHERE tsr.state = 1 AND tsr.tenant_id = #{tenantId}
and so.login_acct like concat('%',#{phone},'%')
and udi.linkman like concat('%',#{linkman},'%')
and tsr.employee_number like concat('%',#{number},'%')
and tsr.staff_position like concat('%',#{position},'%')
and tsr.lock_flag = #{lockFlag}
and r.org_id =#{orgId}
GROUP BY udi.id ORDER BY tsr.create_time DESC

SELECT * FROM tenant_staff_rel a
left join user_data_info b on a.user_info_id= b.id


        a.lock_flag = #{lockFlag}


        and a.tenant_id = #{tenantId}


        and a.user_info_id = #{userAccount}


        and a.staff_name like  concat('%',#{staffName},'%')

   and  a.state = 1

SELECT * FROM tenant_staff_rel a
left join user_data_info b on a.user_info_id= b.id


        lock_flag = #{lockFlag}


        and tenant_id = #{tenantId}


        and user_info_id = #{userAccount}


        and staff_name like  concat('%',#{staffName},'%')
