mapper.sql:
SELECT IFNULL (( SELECT company_name FROM cm_customer_info cmi WHERE cmi.id = customer_id ),'三方') AS customerFullName, receivable_date AS receivableDate, SUM( amount_receivable ) AS amountReceivable, SUM( received_normal ) AS receivedNormal, SUM( received_overdue ) AS receivedOverdue, SUM( uncollected_normal ) AS uncollectedNormal, SUM( uncollected_overdue ) AS uncollectedOverdue FROM daily_accounts_receivable WHERE DATE_FORMAT( create_time, '%Y-%m-%d' ) = DATE_FORMAT( NOW(), '%Y-%m-%d' ) AND tenant_id = #{tenantId}
AND customer_id IN ( ${ids} )
AND DATE_FORMAT( receivable_date, '%Y-%m' ) = #{month} GROUP BY customer_id, receivable_date ORDER BY receivable_date DESC
